INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'O vetor de transmissão da doença de chagas é o bicho barbeiro, que transmite a doença por meio de suas fezes que são levadas até o local da picada', 'O bicho barbeiro transmite a doença, pois sua saliva contém os protozoários causadores da doença (Trypanossoma cruzi)', 'O bicho barbeiro transmite a doença de chagas', 'O mosquito barbeiro transmite a doença de chagas', 'A doença de chagas é transmitida pelo bicho barbeiro (um percevejo e não mosquito), que ao depositar suas fezes contamindas com com o protozoário transmite a doença. O protozoário não se encontra na saliva', 8);
INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'A malária ou maleita é causada pelo Plasmodium sp', 'A malária ou maleita é causada por vírus', 'O mosquito-prego (Anopheles) é o vetor transmissor da malária ou maleita', 'A malária ou maleita é outra doença transmitida pelo Aedes aegypt', 'A malária ou maleita é causada pelo Plasmodium sp, um protozoário esporozoário, e é transmitida pelo mosquito-prego (Anopheles)', 9);
