INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'O efeito estufa é um fenômeno natural que mantém o planeta aquecido', 'O efeito estufa é um fenômeno causado pelo homem e pode trazer sérias consequências para o homem', 'Efeito estufa e aquecimento global são fenômenos distintos', 'Efeito estufa e aquecimento global são sinônimos', 'O efeito estufa é um fenômeno natural que torna a Terra habitável, e não pode ser confundido com o aquecimento global, causado pela emissão de gases estufa pelo homem', 1);
