INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'Jeca Tatu, personagem da literatura de Monteiro Lobato, tem como uma de suas característica a enfermidade amarelão ', 'Jeca Tatu, personagem da literatura de Monteiro Lobato, tem como uma de suas característica a enfermidade doença de chagas', 'O amarelão é provada por vermes que rasgam as paredes do intestino, causando hemorragias e anemias', 'O amarelão é provada por vírus que causam anemia e deixam a pele amarelada', 'O amarelão é causado por vermes (Necator americanus e Ancylostoma  duodenale) e é a doença retratada por Monteiro Lobato no personagem Jeca Tatu. Nessa parasitose, o verme rasga as paredes do intestino, causando hemorragias e anemias', 2);
