INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'Ausência de núcleo é a principal característica das células procariontes', 'Ausência de núcleo é a principal característica das células eucariontes', 'Células procariontes não possuem núcleo', 'Células eucariontes não possuem núcleo', 'As células procariontes têm como característica principal a ausência de núcleo', 1);
