INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'As moléculas de proteínas e lípides que formam a membrana plasmática não são fixas', 'As moléculas de proteínas e lípides que formam a membrana plasmática são fixas', 'A membrana plasmática, pelo modelo do mosaico fluído, apresenta moléculas de proteínas que se movimentam em uma dupla camada fluida de lípides', 'A membrana plasmática é formada apenas por moléculas de proteínas', 'A membrana plasmática é formada por moléculas de proteínas e lipides e, pelo modelo do mosaico fluído, apresenta moléculas de proteínas que se movimentam em uma dupla camada fluida de lípides', 1);
INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'Microvilosidades, invaginação da base, desmossomos, interdigitações e cutícula são especializações da membrana plasmática', 'A membrana plasmática não apresenta especializações', 'A membrana plasmática apresenta várias especializações para, por exemplo, aumentar a supeperfície de absorção, adesão, etc ', 'A membrana plasmátca de todas as células são idênticas', 'Microvilosidades, invaginação da base, desmossomos, interdigitações e cutícula são especializações da membrana plasmática, que aumentam a adesão, superfície de contato, etc', 2);
