INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'A membrana plasmática apresenta dois tipos de transporte: transporte ativo e passivo', 'A membrana plasmática apresenta apenas transporte passivo', 'A regulação que a membrana faz das trocas entre o meio e a célula é a permeabilidade seletiva', 'A permeabilidade seletiva é a capacidade da célula de fazer fagocitose apenas dos alvos desejados', 'A permeabilidade seletiva é a regulação de trocas realizada entre o meio e o interior das células pela membrana plasmática. Esse transporte de substância pode ser ativo ou passivo', 1);
