INSERT INTO id4147366_vestapp.true_false_problem(true_statement, false_statement, true_complementary_statement, false_complementary_statement, solution, sequency) VALUES( 'Proteínas transportadoras aparecem na membrana plasmática e são responsáveis pelo transporte de substâncias específicas', 'Proteínas transportadoras aparecem no hialoplasma e são responsáveis pelo transporte de aminoácidos', 'Proteínas transportadoras podem atuar no transporte ativo e passivo na membrana plasmática', 'Proteínas transportadoras atuam no transporte na membrana plasmática e sempre consomem ATP', 'Proteínas transportadoras são macromoléculas especializadas no transporte de substâncias específicas que atuam no transporte ativo e passivo da membrana plasmática', 5);
